import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { useEffect, useState } from "react";
import useHttp from "../../CustomHooks/use-http";

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const {isLoading, error, httpRequest: fetchMeals} = useHttp()

  useEffect(() => {
      const tranformMeals = (mealsObj) => {
        const loadedMeals = []; //! To putting the incoming object one by one in this array.

        for (const key in responseData) {
          loadedMeals.push({
            id: key,
            name: responseData[key].name,
            description: responseData[key].description,
            price: responseData[key].price,
          });
        }
        setMeals(loadedMeals);
        setisLoading(false);
      }
      
  }, []);

  if (isLoading) {
    return <p className={classes.mealsLoading}>Loading....</p>;
  }

  if (error) {
    return <p className={classes.mealsError}>{error}</p>;
  }
  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
