import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { useEffect, useState } from "react";

const AvailableMeals = () => {
  const [meals, setMeals] = useState([]);
  const [isLoading, setisLoading] = useState(true);
  const [error, setError] = useState(null)

  useEffect(() => {
    const fetchMeals = async () => {
      const response = await fetch(
        "https://food-cart-react-default-rtdb.firebaseio.com/meals"
      );

      //! If the reponse throw an error.
      if (!response.ok) {
        throw new Error("Something went wrong. Please try again!");
      }

      const responseData = await response.json(); //! This is an object only
      const loadedMeals = []; //! To putting the incoming object one by one in this array.

      for (const key in responseData) {
        loadedMeals.push({
          id: key,
          name: responseData[key].name,
          description: responseData[key].description,
          price: responseData[key].price,
        });
      }
      setMeals(loadedMeals);
      setisLoading(false);
    };
    fetchMeals().catch((error) => {
      setisLoading(false)
      setError(error)
    });
  }, []);

  if (isLoading) {
    return <p className={classes.mealsLoading}>Loading....</p>;
  }

  if(error){
    return <p className = {classes.mealsError}>{error}</p>
  }
  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
